mapper.select:
SELECT *, DATE_FORMAT(ADMISSION_TIME ,'%Y-%m-%d %H:%i:%s') admissionTime, DATE_FORMAT(START_TIME ,'%Y-%m-%d %H:%i:%s') startTime, DATE_FORMAT(END_TIME ,'%Y-%m-%d %H:%i:%s') endTime FROM yuyue_site where 1=1
and id = #{id}
ORDER BY START_TIME DESC LIMIT #{begin},#{limit}

SELECT *,
DATE_FORMAT(ADMISSION_TIME ,'%Y-%m-%d %H:%i:%s') admissionTime,
DATE_FORMAT(START_TIME ,'%Y-%m-%d %H:%i:%s') startTime,
DATE_FORMAT(END_TIME ,'%Y-%m-%d %H:%i:%s') endTime
FROM yuyue_site where 1=1

    and siteAddr = #{siteAddr}


    and  status = #{status}


    and  jPushStatus = #{jPushStatus}


    and  mainPerson = #{mainPerson}


    and START_TIME  >=#{startTime}  AND END_TIME <= #{endTime}

ORDER BY START_TIME DESC
LIMIT #{begin},#{limit}
